import React, { useEffect, useState } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";

function BotsPage() {
  const [bots, setBots] = useState([]);
  const [botArmy, setBotArmy] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8002/bots")
      .then(response => response.json())
      .then(data => setBots(data));
  }, []);

  const handleAddToArmy = (bot) => {
    setBotArmy([...botArmy, bot]);
  };

  const handleRemoveFromArmy = (bot) => {
    setBotArmy(botArmy.filter(b => b.id !== bot.id));
  };

  const handleDischargeBot = (botId) => {
    fetch(`http://localhost:8002/bots/${botId}`, { method: "DELETE" })
      .then(() => {
        setBotArmy(botArmy.filter(b => b.id !== botId));
        setBots(bots.filter(b => b.id !== botId));
      });
  };

  return (
    <div>
      <YourBotArmy botArmy={botArmy} onRemoveFromArmy={handleRemoveFromArmy} onDischargeBot={handleDischargeBot} />
      <BotCollection bots={bots} onAddToArmy={handleAddToArmy} />
    </div>
  );
}

export default BotsPage;
