// src/components/BotsPage.js
import React, { useState, useEffect } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";
import BotSpecs from "./BotSpecs";

function BotsPage() {
  const [bots, setBots] = useState([]);
  const [selectedBot, setSelectedBot] = useState(null);
  const [botArmy, setBotArmy] = useState([]);

  useEffect(() => {
    // Fetch bots data from db.json
    fetch("http://localhost:3001/bots") // Adjust the URL if needed
      .then((response) => response.json())
      .then((data) => setBots(data.bots));
  }, []);

  const handleBotClick = (bot) => {
    setSelectedBot(bot);
  };

  const handleEnlist = (bot) => {
    setBotArmy([...botArmy, bot]);
    setSelectedBot(null); // Optionally clear selected bot
  };

  return (
    <div>
      <YourBotArmy botArmy={botArmy} />
      <BotCollection bots={bots} onBotClick={handleBotClick} />
      {selectedBot && <BotSpecs bot={selectedBot} onEnlist={handleEnlist} />}
    </div>
  );
}

export default BotsPage;
