import React, { useState } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";
import BotSpecs from "./BotSpecs"; // Import BotSpecs

function BotsPage() {
  const [botArmy, setBotArmy] = useState([]);
  const [selectedBot, setSelectedBot] = useState(null);

  const handleEnlist = (bot) => {
    if (!botArmy.find(b => b.id === bot.id)) {
      setBotArmy([...botArmy, bot]);
    }
  };

  const handleRelease = (bot) => {
    setBotArmy(botArmy.filter(b => b.id !== bot.id));
  };

  const handleViewBot = (bot) => {
    setSelectedBot(bot);
  };

  const handleBack = () => {
    setSelectedBot(null);
  };

  return (
    <div>
      {selectedBot ? (
        <BotSpecs bot={selectedBot} onGoBack={handleBack} onEnlist={handleEnlist} />
      ) : (
        <>
          <YourBotArmy armyBots={botArmy} onRelease={handleRelease} />
          <BotCollection onEnlist={handleEnlist} onViewBot={handleViewBot} />
        </>
      )}
    </div>
  );
}

export default BotsPage;
