import React, { useState } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";
import BotSpecs from "./BotSpecs";  // Import BotSpecs here

function BotsPage() {
  const [armyBots, setArmyBots] = useState([]);
  const [currentView, setCurrentView] = useState("collection");
  const [selectedBot, setSelectedBot] = useState(null);

  const handleEnlist = (bot) => {
    if (armyBots.find(b => b.bot_class === bot.bot_class)) {
      alert("You already have a bot of this class.");
      return;
    }
    setArmyBots([...armyBots, bot]);
  };

  const handleRelease = (bot) => {
    setArmyBots(armyBots.filter(b => b.id !== bot.id));
  };

  const handleViewBot = (bot) => {
    setSelectedBot(bot);
    setCurrentView("specs");
  };

  const handleGoBack = () => {
    setCurrentView("collection");
    setSelectedBot(null);
  };

  return (
    <div>
      {currentView === "collection" ? (
        <BotCollection
          onEnlist={handleEnlist}
          onViewBot={handleViewBot}
        />
      ) : (
        selectedBot && (
          <BotSpecs
            bot={selectedBot}
            onGoBack={handleGoBack}
            onEnlist={handleEnlist}
          />
        )
      )}
      <YourBotArmy
        armyBots={armyBots}
        onRelease={handleRelease}
      />
    </div>
  );
}

export default BotsPage;
